mapper.select:
select DISTINCT d.agent_name,d.big_status_name,d.project_code,d.is_check,d.is_dele,d.is_fail,d.is_zgys,d.material_type_name, d.project_name,DATE_FORMAT(d.project_time,'%Y-%m-%d %H:%i:%s') project_time,d.purchase_style_id,d.purchase_style_name,d.small_status_name,d.staff_name from project_detail_info d
,project_expert_info e,expert_info i
d.project_id = e.project_id and e.expert_id=i.user_id and
is_zgys='1'
and i.user_id=#{user_id}
and d.agent_id=#{user_id}
and d.staff_id=#{user_id}
and e.expert_id=#{user_id}
order by project_time desc

select DISTINCT d.agent_name,d.big_status_name,d.project_code,d.is_check,d.is_dele,d.is_fail,d.is_zgys,d.material_type_name, d.project_name,DATE_FORMAT(d.project_time,'%Y-%m-%d %H:%i:%s') project_time,d.purchase_style_id,d.purchase_style_name,d.small_status_name,d.staff_name from project_detail_info d
,project_expert_info e,expert_info i
d.project_id = e.project_id and e.expert_id=i.user_id and
is_zgys='0'
and i.user_id=#{user_id}
and d.agent_id=#{user_id}
and d.staff_id=#{user_id}
and e.expert_id=#{user_id}
order by project_time desc

select company,expert_level_name,user_name,user_id from EXPERT_INFO
user_id=#{user_id}
order by update_time desc

select business_range,communication_address,company_property_name,contact_person_email,contact_person_mobile, fax,found_time,legal_person,paiclup_capital,phone,registry_address,registry_fund,state_name,stock_plac, stock_status,supplier_name,supplier_id,supplier_type_name,user_id from supplier_info
user_id=#{user_id}
order by update_time desc

select bank_account,code_type_id,user_name,user_id from AGENT_INFO


		user_id=#{user_id}


order by update_time desc

SELECT notice_title,pub_time,content from
system_notice_info
WHERE _id=#{id}
